            await mostrarMensajes.MostrarMensajeError("Debe de loguearse o registrarse para poder votar");
        }
    }

    private void onmouseoverHandle(int numeroEstrella)
    {
        if (!votado)
        {
            PuntajeSeleccionado = numeroEstrella;
        }
    }

